import React from 'react';

import './news.css'
import News from '../news/allNews/news'

class NewsRoute extends React.Component {
    state = {
        news: []
    };


    componentDidMount = () =>{
        // getNews = () => {
            const newsApi = new NewsAPI();
            newsApi.getNews()
                .then((res) => {
                    console.log(res)
                    this.setState({
                        news: res.data
                    })
                })
                .catch(err => console.log(err))
        // }
    }

    render() {

        return (
            <div>
                {this.props.allNews.map((news) => (
                    <News title={news.title}  content={news.content}/>

                ))}

            </div>
        );
    }
}

export default NewsRoute;
