import React, { Component } from 'react';
import Login from '../User/Login';
import News from '../news/allNews/news'
import NewsRoute from '../news/newsRouts'
import NewsAPI from '../api/news/newsApi'

class Routes extends Component {
    state = {
        authentication: false,
        route: this.props.route,
        userData: 0,
        news: ''
    }


    authentication = (userAllowed, userId) => {
        this.setState({
            authentication: userAllowed,
            userData: userId
        })
        this.props.loggedUserFunc(userAllowed)
    }

    getNews = () => {
        const newsApi = new NewsAPI();
        newsApi.getNews()
            .then(res => {
                console.log(res)
                this.setState({
                    news: res.data
                })
            })
            .catch(err => console.log(err))
    }


    render() {
        console.log(this.state.news)
        return (
            <div>

                {!this.state.authentication ?
                    <Login getAuthorized={(allowedStatus, userId) => this.authentication(allowedStatus, userId)} />
                    :
                    this.props.route == 'All News' ?
                        <NewsRoute onClick={this.getNews()} allNews={this.state.news} /> :
                        'dasd'
                }
            </div>


        );
    }
}

export default Routes;
