import React from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

import './SigIn.css'

import LoginAPI from '../api/login/loginApi'

class SignIn extends React.Component {
    state = {
        firstName: '',
        lastName: '',
        login: '',
        pass: ''
    };

    handleLogin = () => {

        const loginAPI = new LoginAPI();
        loginAPI.postLogin(this.state.userName, this.state.userPass)
            .then(res => {
                console.log(res.data)
                window.localStorage.setItem('jwt', res.data.token);
                this.props.getAuthorized(true, res.data.userId, res.data.roleId)
            })
            .catch(err => {
                console.log(err)
                localStorage.clear()
            })
    }

    handleChange = name => event => {
        this.setState({ [name]: event.target.value });
    };

    render() {

        return (
            <div className="page-container">
                <form className="form-container">
                    <TextField
                        id="standard-name"
                        label="Fisrt Name"
                        value={this.state.firstName}
                        onChange={this.handleChange('firstName')}
                        margin="normal"
                    />
                    <TextField
                        id="standard-name"
                        label="Last Name"
                        value={this.state.lastName}
                        onChange={this.handleChange('lastName')}
                        margin="normal"
                    />
                    <TextField
                        id="standard-name"
                        label="Login"
                        value={this.state.login}
                        onChange={this.handleChange('login')}
                        margin="normal"
                    />
                    <TextField
                        id="standard-name"
                        label="Pass"
                        value={this.state.pass}
                        onChange={this.handleChange('pass')}
                        margin="normal"
                    />
                  
                    <Button onClick={this.handleLogin} color="secondary" variant="contained" className="button">
                        Register
                    </Button>
                </form>
            </div>
        );
    }
}

export default SignIn;
